contract StakingContract is ReentrancyGuard, Ownable {
    struct StakerInfo {
        uint256 depositedAmount;
        uint256 timeOfLastUpdate;
        uint256 endTime;
    }

   
    uint256 public stakersCount = 0;
    uint256 public unstakersCount = 0;
    // uint256 public minStake = 1 ether; // Minimum stake amount: 1 Ether
    uint256 public lockPeriod = 1;
    uint256 public apy = 50;
    address Owner;
    

    mapping(address => StakerInfo) public stakers;
    event stake(uint amount, address addr, uint stakingtime,uint unstakingTime);
    event unstake(uint reward,address addr,uint unstakingTime);

    ERC20 public token;

    constructor(address _token) {
        token = ERC20(_token);
  Owner = msg.sender;
    }

    function Stake() public payable nonReentrant {
         require(msg.value >= 1 wei, "Minimum stake not met");
        stakers[msg.sender].depositedAmount += msg.value;
        stakers[msg.sender].timeOfLastUpdate = block.timestamp;
        stakers[msg.sender].endTime = block.timestamp + 60;
        stakersCount++;

        emit stake(msg.value,msg.sender,block.timestamp,block.timestamp+60);
    }
    function checkAPY()public view returns(uint){
        return apy;
    }
    function checkOwner()public view returns(address){
        return Owner;
    }

    function unStake() public nonReentrant {
        require(stakers[msg.sender].depositedAmount > 0,"first you need to stake ethers");
        uint256 rewards = calculateRewards(msg.sender);
        // require(rewards > 0, "No rewards to claim");
        uint256 depositedAmount = stakers[msg.sender].depositedAmount;
        token.transferFrom(owner(), msg.sender, rewards);
        payable(msg.sender).transfer(depositedAmount);
        emit unstake(rewards, msg.sender, block.timestamp);
        // stakers[msg.sender].timeOfLastUpdate = block.timestamp;
        // stakers[msg.sender].depositedAmount = depositedAmount;
        delete stakers[msg.sender];
 
        unstakersCount++;
    }

    function getDepositInfo(address _user) public view returns (uint256, uint256) {
        uint256 rewards = calculateRewards(_user);
        uint256 depositedAmount = stakers[_user].depositedAmount;
        return (rewards, depositedAmount);
    }

    function calculateRewards(address _staker) public view returns (uint256) {
        if (block.timestamp >= stakers[_staker].endTime) {
            uint256 timeElapsed = stakers[_staker].endTime - stakers[_staker].timeOfLastUpdate;
            uint256 rewards = (timeElapsed * stakers[_staker].depositedAmount * apy) / 100;
            return rewards;
        } else {
            return 0;
        }
    }

    function checkContractBalance()public view returns(uint){
        return address(this).balance;
    }
